import{_ as e,o as l,c as a,B as n}from"./app.dab88326.js";const m=JSON.parse('{"title":"DNS","description":"","frontmatter":{"title":"DNS","author":"Potter","date":"2022/08/05 21:25","tags":["计算机网络","DNS"],"categories":["计算机网络"]},"headers":[{"level":2,"title":"dig 指定DNS 查询特定域名IP","slug":"dig-指定dns-查询特定域名ip","link":"#dig-指定dns-查询特定域名ip","children":[]},{"level":2,"title":"域名树状结构","slug":"域名树状结构","link":"#域名树状结构","children":[]},{"level":2,"title":"域名查询IP","slug":"域名查询ip","link":"#域名查询ip","children":[]},{"level":2,"title":"DNS服务器种类","slug":"dns服务器种类","link":"#dns服务器种类","children":[{"level":3,"title":"递归DNS服务器","slug":"递归dns服务器","link":"#递归dns服务器","children":[]},{"level":3,"title":"权威DNS服务器","slug":"权威dns服务器","link":"#权威dns服务器","children":[]}]},{"level":2,"title":"四种服务器的关系","slug":"四种服务器的关系","link":"#四种服务器的关系","children":[]},{"level":2,"title":"参考文献","slug":"参考文献","link":"#参考文献","children":[]}],"relativePath":"article/01-basic/01-network/05-DNS.md","lastUpdated":1691986431000}'),s={name:"article/01-basic/01-network/05-DNS.md"},i=n(`<h1 id="dns" tabindex="-1">DNS <a class="header-anchor" href="#dns" aria-hidden="true">#</a></h1><h2 id="dig-指定dns-查询特定域名ip" tabindex="-1">dig 指定DNS 查询特定域名IP <a class="header-anchor" href="#dig-指定dns-查询特定域名ip" aria-hidden="true">#</a></h2><blockquote><p>提示：<strong><strong><a href="https://phoenixnap.com/kb/dig-windows" target="_blank" rel="noreferrer">How to Install Dig on Windows</a></strong></strong></p></blockquote><ul><li><p>用法</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">dig</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">@[dns</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">server</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ip]</span><span style="color:#A6ACCD;"> [domain </span><span style="color:#C3E88D;">name]</span></span>
<span class="line"></span>
<span class="line"><span style="color:#FFCB6B;">//example</span></span>
<span class="line"><span style="color:#FFCB6B;">//dig</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">@1.1.1.1</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">google.com</span></span>
<span class="line"></span></code></pre></div></li><li><p>示例</p><p><img src="https://cdn.jsdelivr.net/gh/yxw007/BlogPicBed@master//img/20230313111513.png" alt=""></p></li></ul><h2 id="域名树状结构" tabindex="-1">域名树状结构 <a class="header-anchor" href="#域名树状结构" aria-hidden="true">#</a></h2><ul><li><p>图示</p><p><img src="https://cdn.jsdelivr.net/gh/yxw007/BlogPicBed@master//img/20230313111529.png" alt=""></p></li><li><p>根域名：所有域名的起点</p></li><li><p>顶级域名：根域名的下一级域名，分成两种：通用顶级域名（.com、.net）和国别顶级域名（.cn、.us）</p></li><li><p>一级域名：顶级域名的下一级域名。比如：google.com</p></li><li><p>二级域名：一级域名的下一级域名，域名拥有者可自行设置。比如：news.google.com</p></li></ul><h2 id="域名查询ip" tabindex="-1">域名查询IP <a class="header-anchor" href="#域名查询ip" aria-hidden="true">#</a></h2><p>域名查询ip是逐级查询的，<strong>只有上级域名，才知道下一级域名的 IP 地址</strong></p><p>比如：查询二级域名news.google.com的ip</p><ul><li>第一步：查询根域名服务器，获得顶级域名服务器(.com)ip地址</li><li>第二步：根据顶级域名服务器，获取一级域名服务器(google.com)IP地址</li><li>第三步：根据一级域名服务器，获取二级域名服务器(news.google.com)IP地址</li></ul><h2 id="dns服务器种类" tabindex="-1">DNS服务器种类 <a class="header-anchor" href="#dns服务器种类" aria-hidden="true">#</a></h2><ul><li>1.1.1.1（递归DNS服务器）</li><li>根域名服务器</li><li>顶级域名(TLD)服务器</li><li>一级域名服务器（权威域名服务器）</li></ul><h3 id="递归dns服务器" tabindex="-1">递归DNS服务器 <a class="header-anchor" href="#递归dns服务器" aria-hidden="true">#</a></h3><p>比如：1.1.1.1 直接自动递归查下域名ip，无需手动逐级查询获得ip</p><ul><li><p>示例</p><p><img src="https://cdn.jsdelivr.net/gh/yxw007/BlogPicBed@master//img/20230313111547.png" alt=""></p></li></ul><h3 id="权威dns服务器" tabindex="-1">权威DNS服务器 <a class="header-anchor" href="#权威dns服务器" aria-hidden="true">#</a></h3><p>一级域名服务器，正式名称：权威域名服务器（Authoritative Name Server）</p><p>&quot;权威&quot;的意思是域名的 IP 地址由它给定，不像递归服务器自己做不了主。我们购买域名后，设置 DNS 服务器就是在设置该域名的权威服务器</p><h2 id="四种服务器的关系" tabindex="-1">四种服务器的关系 <a class="header-anchor" href="#四种服务器的关系" aria-hidden="true">#</a></h2><ul><li>图示 <img src="https://cdn.jsdelivr.net/gh/yxw007/BlogPicBed@master//img/20230313111606.png" alt=""></li></ul><h2 id="参考文献" tabindex="-1">参考文献 <a class="header-anchor" href="#参考文献" aria-hidden="true">#</a></h2><ul><li><strong><strong><a href="https://phoenixnap.com/kb/dig-windows" target="_blank" rel="noreferrer">How to Install Dig on Windows</a></strong></strong></li><li><a href="http://www.ruanyifeng.com/blog/2022/08/dns-query.html" target="_blank" rel="noreferrer">http://www.ruanyifeng.com/blog/2022/08/dns-query.html</a></li></ul>`,22),t=[i];function r(o,p,d,c,h,g){return l(),a("div",null,t)}const D=e(s,[["render",r]]);export{m as __pageData,D as default};
